import org.apache.nifi.processor.io.InputStreamCallback
import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets

def flowFile = session.get()
if (!flowFile) return

try {
    // 1. Leer el contenido del FlowFile (la lnea ya formateada por ReplaceText)
    def linea = ""
    session.read(flowFile, { inputStream ->
        linea = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
    } as InputStreamCallback)

    // 2. Asegurarnos de que termina en salto de lnea
    if (!linea.endsWith(System.lineSeparator())) {
        linea = linea + System.lineSeparator()
    }

    // 3. Obtener la ruta del fichero de log desde la propiedad LOG_FILE
    def logPath = context.getProperty("LOG_FILE").evaluateAttributeExpressions(flowFile).getValue()
    if (!logPath) {
        logPath = "/var/log/nifi/errores.log"   // por si se te olvida poner la propiedad
    }

    def logFile = new File(logPath)
    logFile.parentFile?.mkdirs()   // crea el directorio si no existe

    // 4. Hacer append al fichero errores.log
    logFile.withWriterAppend("UTF-8") { writer ->
        writer << linea
    }

    // 5. Mandar el FlowFile a success (o a otro sitio si quieres)
    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error("Error escribiendo en errores.log", e)
    session.transfer(flowFile, REL_FAILURE)
}

